import Link from "next/link";
import { motion } from "framer-motion";
import React, { useState, useEffect } from "react";
import Typical from 'react-typical';

let typedText = ""; 
const textArray = ["hard", "fun", "please", "work"]; // word array
const typingDelay = 200;
const erasingDelay = 100;
const newTextDelay = 2000; // Delay between current and next text
let textArrayIndex = 0;
let charIndex = 0;


export default function Home() {

let [message, setMessage] = useState("");
let [typingState, setTypingState] = useState(false);

  useEffect(() => {

    if(textArray.length) setTimeout(type, newTextDelay + 250);

  }, [])


let type = () => {
  if (charIndex < textArray[textArrayIndex].length) {
    if(!typingState) setTypingState(true); 
    //change cursor State to typing
    setMessage(typedText += textArray[textArrayIndex].charAt(charIndex));
    charIndex++;
    setTimeout(type, typingDelay);
  } 
  else {
    setTypingState(false);
  	setTimeout(erase, newTextDelay);
}

function erase() {
	if (charIndex > 0) {
    if(!typingState) setTypingState(true); 
    setMessage(textArray[textArrayIndex].substring(0, charIndex-1));
    charIndex--;
    setTimeout(erase, erasingDelay);
  } 
  else {
    setTypingState(false);
    setMessage("")
    textArrayIndex++;
    if(textArrayIndex >= textArray.length) textArrayIndex=0;
    setTimeout(type, typingDelay + 1100);
  }
}


  }

  return (

   <div className="">

<div className="flex h-screen justify-center items-center">
  <div className="flex flex-col">
     <p className="fontmain "> Coding is <span>{message}</span> 
    </p>
    </div>
   </div>

   <div className="m-72">
      <motion.h1 layoutId="header" className="mb-20 font-custom4 text-7xl">My projects</motion.h1>
      <div className="object-cover mt-50 container flex flex-row justify-around">
        {[{name: "midori", color: "blue"}, {name: "shareit", color: ""}].map((product) => (
          <Link href={product}>
            <a>
              <motion.img
                layoutId={product.name}
                className={`border rounded ${"shadow-sm" + product.color} mr-10 w-1/2`}
                src={product.name + ".png"}
                animate={{ scale: 1 }}
                whileHover={{ scale: 1.1 }}
              />

            </a>
          </Link>
        ))}
      </div>
    </div>
    </div>
  );
}
