import React from 'react';
import { motion, AnimatePresence } from "framer-motion";
import { useInView } from 'react-intersection-observer';
import SplitText from '.SplitText'

export default function Contact() {


    const { ref, inView, entry } = useInView({
        /* Optional options */
        threshold: 0,
      });

      const container = {
        hidden: { opacity: 0 },
        show: {
          opacity: 1,
          animate: { x: 50 },
          transition: {
            staggerChildren: 0.5,
          },
        },
      };

      const letter = {
        hidden: { opacity: 0 },
        show: { opacity: 1 },
      };

    return (
        <div className="flex justify-center items-center h-screen bg-hero-bg2 mt-64 text-center fontmain">
   <div ref={ref}>
      <h2>{`Header inside viewport ${inView}.`}</h2>

      <h1>
        <AnimatePresence>
          {inView && (
            <motion.div
              initial={{ opacity: 1 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
            >
              <SplitText
                initial={{ y: '100%' }}
                animate="visible"
                variants={{
                  visible: i => ({
                    y: 0,
                    transition: {
                      delay: i * 0.1
                    }
                  })
                }}
              >
                Hello CodeSandbox Lorem Ipsum Dolor Sit Amet
              </SplitText>
            </motion.div>
          )}
        </AnimatePresence>
      </h1>



    </div>

<p className="text-2xl"> whaaaat {inView}</p>

        </div>
    )
}
