import React, { useState, useEffect } from "react";
import Typist from "react-typist";

export default function TypeWriter(props) {
  const [count, setCount] = useState(1);

  useEffect(() => {

    setCount(1);
  }, [count]);

  return (
    <div className="fontmain text-center text-7xl">
      <p className=" extra-loose m-auto text-grey-800 specialunderline bg-highlightsize bg-highlightposition">
        {props.language == 'EN' ? "hi I am" : props.language == 'ES' ? "hola soy" : "olà sou"} <span className="wavy">samantha</span> {},a <br />{" "}
        <span className="font-black"> {props.language == 'EN' ? "full stack developer" : props.language == 'ES' ? "desarrolladora full-stack" : "full stack developer"}</span>{" "}
      </p>

      <div className=" fontmain text-7xl flex flex-row justify-center font-5xl gap-5">
        <p> {props.language == 'EN' ? "and a" : props.language == 'ES' ? "y" : "e"} </p>
        <p className="fontmain font-black text-7xl">
          {count ? (
            <Typist avgTypingDelay={100} onTypingDone={() => setCount(0)}>
              <span>
              {props.language == 'EN' ? "language learner" : props.language == 'ES' ? "amante de los idiomas" : "btn-grey"} 
              </span>
              <Typist.Backspace count={20} delay={1000} />
              <span>  {props.language == 'EN' ? "traveller" : props.language == 'ES' ? "viajera" : "btn-grey"} </span>
              <Typist.Backspace count={20} delay={1000} />
              <span>  {props.language == 'EN' ? "knitting pro" : props.language == 'ES' ? "fan de tejer" : "btn-grey"} </span>
              <Typist.Backspace count={20} delay={1000} />
              <span>  {props.language == 'EN' ? "music lover" : props.language == 'ES' ? "melómana" : "btn-grey"} </span>
              <Typist.Backspace count={20} delay={1000} />
              <span> {props.language == 'EN' ? "dog person" : props.language == 'ES' ? "adoro los perros" : "btn-grey"} </span>
            </Typist>
          ) : (
            ""
          )}
        </p>
      </div>

      <p className="fontmain text-7xl"> based in Spain </p>
    </div>
  );
}
