import React from 'react';
import { motion } from "framer-motion";
import { useInView } from 'react-intersection-observer';

export default function Contact() {


    const { ref, inView, entry } = useInView({
        /* Optional options */
        threshold: 0,
      });

  const container = {
    hidden: { opacity: 0 },
    show: {
      opacity: inView ? 1 : 0,

      transition: {
        staggerChildren: 0.4,
        // delayChildren:  0.6,
        // staggerDirection: -1,
      },
    },
  };

  const letter = {
    hidden: { opacity: 0 },
    show: { opacity: inView && 1, transition: { duration: 1 }, animate: { y: inView ? 100 : 0 }, },
  };

    return (
        <div className="flex justify-center items-center h-screen bg-hero-bg2 mt-64 text-center fontmain">
   <div ref={ref}>
      <h2>{`Header inside viewport ${inView}.`}</h2>
      <motion.div variants={container}
            initial="hidden"
            animate="show" className='model'>
              <motion.span className='first' variants={container}>
                <motion.span variants={letter}>Y</motion.span>
                <motion.span variants={letter}>a</motion.span>
                <motion.span variants={letter}>s</motion.span>
                <motion.span variants={letter}>m</motion.span>
                <motion.span variants={letter}>e</motion.span>
                <motion.span variants={letter}>e</motion.span>
                <motion.span variants={letter}>n</motion.span>
              </motion.span>
              <motion.span className='last' variants={container}>
                <motion.span variants={letter}>T</motion.span>
                <motion.span variants={letter}>a</motion.span>
                <motion.span variants={letter}>r</motion.span>
                <motion.span variants={letter}>i</motion.span>
                <motion.span variants={letter}>q</motion.span>
              </motion.span>
            </motion.div>
    </div>

<p className="text-2xl"> whaaaat {inView}</p>

        </div>
    )
}
