import React, { useState, useEffect } from "react";
import ReactDOM from "react-dom";
import Typist from "react-typist";

export default function TypeWriter() {
  const [count, setCount] = useState(1);

  useEffect(() => {
    // document.title = `You clicked ${count} times`;
    console.log("Count: " + count);
    setCount(1);
  }, [count]);

  return (
    <div>

<p className="flex fontmain extra-loose m-auto text-7xl text-center text-grey-800 specialunderline bg-highlightsize bg-highlightposition">
            hi I am <span className="wavy">samantha</span>,a <br />{" "}
            <span className=" font-bold">full-stack developer</span> <br /> and
            a </p><



              Typist avgTypingDelay={50} onTypingDone={() => setCount(0)}>
          <span> language learner</span>
          <Typist.Backspace count={20} delay={800} />
          <span> traveller</span>
         <Typist.Backspace count={20} delay={800} />
         <span> knitting pro</span>
         <Typist.Backspace count={20} delay={800} />
         <span> music lover</span>
         <Typist.Backspace count={20} delay={800} />
         <span> dog person </span>
         </Typist>

    <p> based in Spain </p>
    </div>
  );
}
