import Head from "next/head";
import Link from "next/link";

import { useRouter } from "next/router";

const Style = ({ id }) => {
  // const router = useRouter();
  // const { id } = router.query;

  return (
    <div className=" bg-gradient-to-b from-cyan-400 to-purple-500 h-screen flex items-center justify-center">
      <div className="container w-96">
        <h1 className="text-white text-opacity-75 text-4xl mb-4">
          Style: {id}
        </h1>

        <div className="h-48 w-full bg-white bg-opacity-25 p-4 rounded-lg mb-4 shadow-lg">
          <div
            className={`w-40 h-40 bg-gradient-to-b from-orange-400 to-red-500 rounded-md transform duration-500  ${
              id === "right" ? "translate-x-48 rotate-180" : null
            }`}
          ></div>
        </div>

        <div className="flex justify-between items-center">
          <Link href="/styles/left">
            <a className="px-6 py-3 font-semibold bg-gray-200 text-gray-700 rounded-lg">
              Left
            </a>
          </Link>

          <Link href="/styles/right">
            <a className="px-6 py-3 font-semibold bg-gray-200 text-gray-700 rounded-lg">
              Right
            </a>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Style;

// we tell it which dynamic pages to generate statically
export async function getStaticPaths() {
  return {
    paths: [{ params: { id: "left" } }, { params: { id: "right" } }],
    fallback: false,
  };
}

// if we need params from the route, we can get them like this
export async function getStaticProps({ params }) {
  // params contains the `id`.
  // If the route is like /styles/left, then params.id is `left`

  // Pass data to the page via props
  return {
    props: { id: params.id },
  };
}
