import Link from "next/link";
import { motion } from "framer-motion";
import React, { useState, useEffect } from "react";
import Typical from "react-typical";

export default function Home() {
  let [message, setMessage] = useState("");
  let [typingState, setTypingState] = useState(false);

  useEffect(() => {}, []);

  return (
    <div className="">
      <div className="fixed flex mt-12 flex-col justify-start items-center text-4xl m-auto text-gray-800">
        <div className="vl"></div>
        <div className="mb-4 bg-indigo-200 rounded-full px-3 py-2 hover:bg-gray-800 hover:text-white">
          <a href="http://linkedin.com/in/samantha-pinos/">
            <i class="fab fa-linkedin-in"></i>
          </a>
        </div>

        <div className="mb-4 bg-yellow-200 rounded-full px-3 py-2 hover:bg-gray-800 hover:text-white ">
          <a href="http://github.com/Samanthapv">
            <i className="fab fa-github"></i>
          </a>
        </div>
        <div className="bg-green-200 rounded-full px-3 py-2 hover:bg-gray-800 hover:text-white">
          <a href="mailto:spinosv@gmail.com">
            <i className="fa fa-envelope" aria-hidden="true"></i>
          </a>
        </div>
        <div className="vl"></div>
      </div>

      <div className="flex justify-center items-center bg-hero-bg bg-no-repeat bg-cover h-screen px-1">
        <div>
          <p className="fontmain extra-loose m-auto text-7xl text-center specialunderline bg-highlightsize bg-highlightposition">
            hi I am <span className="wavy">samantha</span>,a <br />{" "}
            <span className=" font-bold">full-stack developer</span> <br /> and
            a{" "}
            <Typical
              loop={Infinity}
              wrapper="b"
              steps={[
                "language learner",
                1000,
                "traveller",
                1000,
                "knitter",
                1000,
                "music lover",
                1000,
              ]}
            />{" "}
            <br /> based in Spain.
          </p>
        </div>
      </div>

      <div className="m-72 h-screen">
        <motion.h1 layoutId="header" className="mb-20 font-custom4 text-7xl">
          My projects
        </motion.h1>
        <div className="object-cover mt-50 container flex flex-row justify-around">
          {[
            { name: "midori", color: "blue" },
            { name: "shareit", color: "" },
          ].map((product) => (
            <Link href={product}>
              <a>
                <motion.img
                  layoutId={product.name}
                  className={`border rounded ${
                    "shadow-sm" + product.color
                  } mr-10 w-1/2`}
                  src={product.name + ".png"}
                  animate={{ scale: 1 }}
                  whileHover={{ scale: 1.1 }}
                />
              </a>
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
}
