import React, { useState } from "react";

export default function LanguagePicker() {

    const [language, setLanguage] = useState("EN")

    return (
        <div className="bg-hero-bg bg-no-repeat bg-cover  w-screen px-1">
        <p className="text-right font4 mr-4 pt-3"> 
        <span className={`hover:text-white cursor-pointer ${language === "EN" && "text-white"}`} onClick={() => setLanguage('EN')}>EN</span> |
        {" "}<span className={`hover:text-white cursor-pointer ${language === "ES" && "text-white"}`} onClick={() => setLanguage('ES')}>ES</span> | 
        {" "}<span className={`hover:text-white cursor-pointer ${language === "PT" && "text-white"}`} onClick={() => setLanguage('PT')}>PT</span></p>
     </div>
    )
}
