import React, { useState, useEffect } from "react";
import Typist from "react-typist";

export default function TypeWriter() {
  const [count, setCount] = useState(1);

  useEffect(() => {

    setCount(1);
  }, [count]);

  return (
    <div className="fontmain text-center">
      <p className=" extra-loose m-auto text-7xl  text-grey-800 specialunderline bg-highlightsize bg-highlightposition">
        hi I am <span className="wavy">samantha</span>,a <br />{" "}
        <span className=" font-black">full-stack developer</span>{" "}
      </p>

      <div className="flex flex-row justify-center g">
        <p> and a </p>
        <p className="font-bold">
          {count ? (
            <Typist avgTypingDelay={50} onTypingDone={() => setCount(0)}>
              <span> language learner</span>
              <Typist.Backspace count={20} delay={800} />
              <span> traveller</span>
              <Typist.Backspace count={20} delay={800} />
              <span> knitting pro</span>
              <Typist.Backspace count={20} delay={800} />
              <span> music lover</span>
              <Typist.Backspace count={20} delay={800} />
              <span> dog person </span>
            </Typist>
          ) : (
            ""
          )}
        </p>
      </div>

      <p> based in Spain </p>
    </div>
  );
}
