    show: {
      opacity: inView ? 1 : 0,
      animate: { x: inView ? 50 : 0 },
      transition: {
        staggerChildren: 0.4,
        delayChildren:  0.6,
        staggerDirection: -1,
      },
    },
  };

  const letter = {
    hidden: { opacity: 0 },
    show: { opacity: inView && 1, transition: { duration: 1 } },
  };

    return (
        <div className="flex justify-center items-center h-screen bg-hero-bg2 mt-64 text-center fontmain">
   <div ref={ref}>
      <h2>{`Header inside viewport ${inView}.`}</h2>

    </div>

<p className="text-2xl"> whaaaat {inView}</p>

        </div>
    )
}
