import { useRouter } from "next/router";

export default function Project() {

  const {
    query: { project },
  } = useRouter();

const image = project + ".png"

  return (
    <div className="container bg-indigo-800 w-full">


      <main className="w-screen">
        <h1 className="text-indigo-300 text-3xl">
        {project}
        </h1>

        <div className="flex flex-row justify-center">
         <img src={image} alt={image} className="w-5/12 h-1/4 border-4 border-light-blue-500 border-opacity-100 ml-3 mt-4"/>
        </div>

      </main>

    </div>
  );
}
